import { Injectable } from '@angular/core';
import {
  Document,
  Paragraph,
  Packer,
  TextRun,
  Header,
  HeadingLevel,
  AlignmentType,
  ImageRun,
  HorizontalPositionRelativeFrom,
  HorizontalPositionAlign,
  VerticalPositionAlign,
  VerticalPositionRelativeFrom,
} from 'docx';
import { saveAs } from 'file-saver';
@Injectable({
  providedIn: 'root',
})
export class DocumentCreatorService {
  create(text: any = []) {
    const image = new ImageRun({
      data: 'data:image/png;base64,',
      transformation: {
        width: 80,
        height: 65,
      },
    });
    const doc = new Document({
      sections: [
        {
          headers: {
            default: new Header({
              children: [new Paragraph('Header text')],
            }),
            first: new Header({
              children: [
                new Paragraph({
                  alignment: AlignmentType.CENTER,
                  children: [
                    image,
                    new TextRun({
                      text: 'STUDIO LEGALE LAUDATO',
                      font: 'Book Antiqua',
                      size: '12pt',
                      color: '000000',
                      break:1
                    }),
                    new TextRun({
                      text: 'Avv. Enrica Laudato',
                      font: 'Book Antiqua',
                      size: '12.5pt',
                      color: '000000',
                    }),
                  ],
                }),
              ],
            }),
          },
          properties: {
            titlePage: true,
          },
          children: text
            .map((te: any) =>
              te.paragraph
                .split('<p>')
                .slice(1)
                .map((p: any, pi: number) => {
                  return new Paragraph({
                    children: p.split(' ').map((t: string, ti: number) => {
                      return new TextRun({
                        text:
                          t.replace(/<strong>|<\/strong>|<p>|<\/p>/g, '') + ' ',
                        bold: t.includes('<strong>'),
                      });
                    }),
                  });
                })
            )
            .flat(),
        },
      ],
    });

    // Used to export the file into a .docx file

    Packer.toBlob(doc).then((blob) => {
      console.log(blob);
      saveAs(blob, 'example.docx');
      console.log('Document created successfully');
    });
  }
  constructor() {}
}
